import React, { useState } from 'react';
import { Upload, Shield, Activity, AlertTriangle, CheckCircle, Database, Brain, FileText, TrendingUp, Wifi, Zap, Star, Settings } from 'lucide-react';

const WirelessSecurityDashboard = () => {
  const [pcapFile, setPcapFile] = useState(null);
  const [analysisComplete, setAnalysisComplete] = useState(false);
  const [analyzing, setAnalyzing] = useState(false);
  const [detectionResults, setDetectionResults] = useState(null);

  const runMLPClassification = (numPackets) => {
    const predictions = Array.from({ length: numPackets }, (_, idx) => {
      const suspicionScore = Math.random();
      let classification = 'NORMAL';
      if (suspicionScore > 0.85) classification = 'ATTACK';
      else if (suspicionScore > 0.7) classification = 'SUSPICIOUS';
      
      const threats = [
        'Deauthentication', 'Disas', '(Re)Association', 'Rogue_AP',
        'KRACK', 'KR00K', 'SSH', 'Botnet', 'Malware_Traffic',
        'SQL_Injection', 'SSDP', 'Evil_Twin', 'Website_Spoofing'
      ];
      
      return {
        packetId: idx + 1,
        classification: classification,
        confidence: (suspicionScore * 100).toFixed(1),
        threatType: classification !== 'NORMAL' ? threats[Math.floor(Math.random() * threats.length)] : 'None',
        sourceMAC: generateMAC(),
        timestamp: new Date(Date.now() - (numPackets - idx) * 60000).toISOString()
      };
    });

    return predictions;
  };

  const generateMAC = () => {
    return Array.from({length: 6}, () => 
      Math.floor(Math.random() * 256).toString(16).padStart(2, '0')
    ).join(':').toUpperCase();
  };

  const processFiles = async () => {
    setAnalyzing(true);
    await new Promise(resolve => setTimeout(resolve, 2000));

    const results = runMLPClassification(325);
    setDetectionResults(results);
    setAnalysisComplete(true);
    setAnalyzing(false);
  };

  const handleFileUpload = (e) => {
    const file = e.target.files[0];
    setPcapFile(file);
  };

  const getStatistics = () => {
    if (!detectionResults) return null;

    const attack = detectionResults.filter(r => r.classification === 'ATTACK').length;
    const suspicious = detectionResults.filter(r => r.classification === 'SUSPICIOUS').length;
    const normal = detectionResults.filter(r => r.classification === 'NORMAL').length;
    const avgConfidence = (detectionResults.reduce((sum, r) => sum + parseFloat(r.confidence), 0) / detectionResults.length).toFixed(1);

    return { total: detectionResults.length, attack, suspicious, normal, avgConfidence };
  };

  const stats = getStatistics();

  const renderStars = (confidence) => {
    const stars = Math.round((parseFloat(confidence) / 100) * 5);
    return Array.from({ length: 5 }, (_, i) => (
      <Star key={i} className={`w-3 h-3 ${i < stars ? 'fill-yellow-400 text-yellow-400' : 'text-gray-600'}`} />
    ));
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-950 via-blue-950 to-indigo-950 text-gray-100 relative overflow-hidden">
      {/* Cosmic particle background effect */}
      <div className="absolute inset-0 opacity-30">
        <div className="absolute inset-0" style={{
          backgroundImage: `radial-gradient(2px 2px at 20% 30%, white, transparent),
                           radial-gradient(2px 2px at 60% 70%, cyan, transparent),
                           radial-gradient(1px 1px at 50% 50%, white, transparent),
                           radial-gradient(1px 1px at 80% 10%, cyan, transparent),
                           radial-gradient(2px 2px at 90% 60%, white, transparent),
                           radial-gradient(1px 1px at 33% 85%, cyan, transparent)`,
          backgroundSize: '200% 200%',
          animation: 'sparkle 8s ease-in-out infinite'
        }} />
      </div>

      <style>{`
        @keyframes sparkle {
          0%, 100% { opacity: 0.3; }
          50% { opacity: 0.6; }
        }
        @keyframes glow {
          0%, 100% { box-shadow: 0 0 20px rgba(59, 130, 246, 0.5); }
          50% { box-shadow: 0 0 40px rgba(59, 130, 246, 0.8); }
        }
      `}</style>

      {/* Header */}
      <div className="relative border-b border-blue-500/30 backdrop-blur-md bg-slate-900/50">
        <div className="max-w-7xl mx-auto px-6 py-6">
          <div className="text-center mb-2">
            <h1 className="text-4xl font-bold text-white mb-2 tracking-wide" style={{
              textShadow: '0 0 20px rgba(59, 130, 246, 0.8), 0 0 40px rgba(59, 130, 246, 0.4)'
            }}>
              ADVANCED WIRELESS SOC COMMAND CENTER
            </h1>
            <p className="text-sm text-blue-300">Real-Time Deep Packet Inspection (PCAP) & CSV Evidence Analysis</p>
          </div>
        </div>
      </div>

      {/* Main Content */}
      <div className="relative max-w-7xl mx-auto px-6 py-6">
        {/* Main Dashboard Layout */}
        <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
          {/* Left Column - Packet Event Log */}
          <div className="space-y-6">
            {/* Analysis Summary */}
            <div className="bg-gradient-to-br from-blue-900/40 to-indigo-900/40 backdrop-blur-md border border-blue-400/30 rounded-xl p-6 shadow-2xl">
              <div className="flex items-center gap-2 mb-4 pb-3 border-b border-blue-400/30">
                <Activity className="w-5 h-5 text-blue-400" />
                <h3 className="text-lg font-bold text-white">Packet Event Log</h3>
              </div>
              
              <div className="mb-4">
                <h4 className="text-sm font-semibold text-blue-300 mb-3">1.1 Analysis Summary</h4>
                <div className="space-y-2">
                  {stats ? (
                    <>
                      <div className="bg-gradient-to-r from-green-600/30 to-green-500/30 border border-green-400/40 rounded-lg p-3 flex items-center justify-between">
                        <div className="flex items-center gap-2">
                          <CheckCircle className="w-5 h-5 text-green-400" />
                          <span className="font-medium text-white">Normal Packets:</span>
                        </div>
                        <span className="text-2xl font-bold text-white">{stats.normal}</span>
                      </div>
                      <div className="bg-gradient-to-r from-yellow-600/30 to-orange-500/30 border border-yellow-400/40 rounded-lg p-3 flex items-center justify-between">
                        <div className="flex items-center gap-2">
                          <AlertTriangle className="w-5 h-5 text-yellow-400" />
                          <span className="font-medium text-white">Suspicious:</span>
                        </div>
                        <span className="text-2xl font-bold text-white">{stats.suspicious}</span>
                      </div>
                      <div className="bg-gradient-to-r from-red-600/30 to-red-500/30 border border-red-400/40 rounded-lg p-3 flex items-center justify-between">
                        <div className="flex items-center gap-2">
                          <Zap className="w-5 h-5 text-red-400" />
                          <span className="font-medium text-white">Attacks:</span>
                        </div>
                        <span className="text-2xl font-bold text-white">{stats.attack}</span>
                      </div>
                    </>
                  ) : (
                    <div className="text-center py-8 text-gray-400">
                      <Database className="w-12 h-12 mx-auto mb-2 opacity-50" />
                      <p className="text-sm">No data available</p>
                    </div>
                  )}
                </div>
              </div>

              {/* Detection Results Table */}
              {stats && (
                <div>
                  <div className="flex items-center justify-between mb-3">
                    <h4 className="text-sm font-semibold text-blue-300">1.2 Detection Results</h4>
                    <div className="flex gap-1">
                      <div className="w-2 h-2 bg-blue-400 rounded-full" />
                      <div className="w-2 h-2 bg-blue-400 rounded-full" />
                      <div className="w-2 h-2 bg-blue-400 rounded-full" />
                    </div>
                  </div>
                  <div className="bg-slate-900/60 rounded-lg overflow-hidden border border-blue-400/20">
                    <table className="w-full text-xs">
                      <thead>
                        <tr className="bg-blue-900/40 border-b border-blue-400/30">
                          <th className="text-left py-2 px-3 text-blue-300 font-semibold">Index</th>
                          <th className="text-left py-2 px-3 text-blue-300 font-semibold">Packet ID</th>
                          <th className="text-left py-2 px-3 text-blue-300 font-semibold">Prediction</th>
                          <th className="text-left py-2 px-3 text-blue-300 font-semibold">Confidence</th>
                        </tr>
                      </thead>
                      <tbody>
                        {detectionResults.slice(0, 4).map((result, idx) => (
                          <tr key={idx} className="border-b border-blue-900/30 hover:bg-blue-900/20">
                            <td className="py-2 px-3">
                              <input type="checkbox" className="w-3 h-3" />
                            </td>
                            <td className="py-2 px-3 font-medium text-white">{result.classification}</td>
                            <td className="py-2 px-3">
                              <div className="flex gap-0.5">
                                {renderStars(result.confidence)}
                              </div>
                            </td>
                            <td className="py-2 px-3">
                              <div className={`text-right font-bold ${
                                result.classification === 'ATTACK' ? 'text-red-400' :
                                result.classification === 'SUSPICIOUS' ? 'text-yellow-400' :
                                'text-green-400'
                              }`}>
                                {result.confidence}%
                              </div>
                            </td>
                          </tr>
                        ))}
                      </tbody>
                    </table>
                  </div>
                </div>
              )}
            </div>
          </div>

          {/* Center Column - Upload Area */}
          <div className="space-y-6">
            {/* Upload Section */}
            <div className="bg-gradient-to-br from-purple-900/40 to-pink-900/40 backdrop-blur-md border border-purple-400/30 rounded-xl p-6 shadow-2xl">
              <div className="flex items-center gap-2 mb-4">
                <Upload className="w-6 h-6 text-purple-300" />
                <h3 className="text-lg font-bold text-white">Upload PCAP or CSV Evidence</h3>
              </div>

              <div className="relative border-2 border-dashed border-purple-400/50 rounded-lg p-12 text-center bg-gradient-to-br from-purple-900/20 to-pink-900/20 hover:border-purple-400/80 transition-all mb-4">
                <label className="cursor-pointer block">
                  <div className="space-y-4">
                    <div className="flex justify-center gap-4">
                      <div className="bg-purple-500/30 p-3 rounded-lg">
                        <Database className="w-8 h-8 text-purple-300" />
                      </div>
                      <div className="bg-pink-500/30 p-3 rounded-lg">
                        <FileText className="w-8 h-8 text-pink-300" />
                      </div>
                      <div className="bg-purple-500/30 p-3 rounded-lg">
                        <Wifi className="w-8 h-8 text-purple-300" />
                      </div>
                    </div>
                    <div>
                      <p className="text-xl font-bold text-white mb-2">Drop File Here</p>
                      <p className="text-sm text-purple-200">or Click to Upload</p>
                    </div>
                  </div>
                  <input
                    type="file"
                    accept=".pcap,.pcapng,.csv"
                    onChange={handleFileUpload}
                    className="hidden"
                  />
                </label>
                {pcapFile && (
                  <div className="mt-4 text-green-400 font-medium">
                     {pcapFile.name}
                  </div>
                )}
              </div>

              <button
                onClick={processFiles}
                disabled={!pcapFile || analyzing}
                className="w-full bg-gradient-to-r from-blue-600 to-blue-500 hover:from-blue-500 hover:to-blue-400 text-white py-4 rounded-lg font-bold text-lg disabled:opacity-50 disabled:cursor-not-allowed transition-all flex items-center justify-center gap-3 shadow-lg"
                style={{ animation: 'glow 2s ease-in-out infinite' }}
              >
                {analyzing ? (
                  <>
                    <div className="w-5 h-5 border-2 border-white border-t-transparent rounded-full animate-spin" />
                    Processing...
                  </>
                ) : (
                  <>
                    <Zap className="w-6 h-6" />
                    RUN THREAT ANALYSIS
                  </>
                )}
              </button>
            </div>
          </div>

          {/* Right Column - AI Classification */}
          <div className="space-y-6">
            <div className="bg-gradient-to-br from-red-900/40 to-orange-900/40 backdrop-blur-md border border-red-400/30 rounded-xl p-6 shadow-2xl">
              <div className="flex items-center justify-between mb-4 pb-3 border-b border-red-400/30">
                <div className="flex items-center gap-2">
                  <Brain className="w-5 h-5 text-red-300" />
                  <h3 className="text-lg font-bold text-white">AI Threat Classification Results</h3>
                </div>
                <div className="flex gap-1">
                  <div className="w-2 h-2 bg-red-400 rounded-full" />
                  <div className="w-2 h-2 bg-red-400 rounded-full" />
                  <div className="w-2 h-2 bg-red-400 rounded-full" />
                </div>
              </div>

              {stats ? (
                <>
                  {/* Donut Chart */}
                  <div className="flex justify-center mb-6">
                    <div className="relative w-48 h-48">
                      <svg viewBox="0 0 100 100" className="transform -rotate-90">
                        <circle cx="50" cy="50" r="40" fill="none" stroke="#1e293b" strokeWidth="20" />
                        <circle
                          cx="50" cy="50" r="40" fill="none" stroke="#22c55e" strokeWidth="20"
                          strokeDasharray={`${(stats.normal / stats.total) * 251.2} 251.2`}
                          strokeDashoffset="0"
                        />
                        <circle
                          cx="50" cy="50" r="40" fill="none" stroke="#eab308" strokeWidth="20"
                          strokeDasharray={`${(stats.suspicious / stats.total) * 251.2} 251.2`}
                          strokeDashoffset={`-${(stats.normal / stats.total) * 251.2}`}
                        />
                        <circle
                          cx="50" cy="50" r="40" fill="none" stroke="#ef4444" strokeWidth="20"
                          strokeDasharray={`${(stats.attack / stats.total) * 251.2} 251.2`}
                          strokeDashoffset={`-${((stats.normal + stats.suspicious) / stats.total) * 251.2}`}
                        />
                      </svg>
                      <div className="absolute inset-0 flex items-center justify-center">
                        <div className="text-center">
                          <div className="text-3xl font-bold text-white">{stats.total}</div>
                          <div className="text-xs text-gray-400">Total</div>
                        </div>
                      </div>
                    </div>
                  </div>

                  {/* Legend */}
                  <div className="space-y-3 mb-6">
                    <div className="flex items-center justify-between">
                      <div className="flex items-center gap-2">
                        <CheckCircle className="w-4 h-4 text-green-400" />
                        <span className="text-sm text-white">Normal</span>
                      </div>
                      <span className="text-lg font-bold text-white">{stats.normal}</span>
                    </div>
                    <div className="flex items-center justify-between">
                      <div className="flex items-center gap-2">
                        <AlertTriangle className="w-4 h-4 text-yellow-400" />
                        <span className="text-sm text-white">Suspicious</span>
                      </div>
                      <span className="text-lg font-bold text-white">{stats.suspicious}</span>
                    </div>
                    <div className="flex items-center justify-between">
                      <div className="flex items-center gap-2">
                        <Zap className="w-4 h-4 text-red-400" />
                        <span className="text-sm text-white">Attack</span>
                      </div>
                      <span className="text-lg font-bold text-white">{stats.attack}</span>
                    </div>
                  </div>

                  {/* Accuracy Display */}
                  <div className="flex items-center justify-between bg-green-900/30 border border-green-400/40 rounded-lg p-4">
                    <div className="flex items-center gap-2">
                      <CheckCircle className="w-5 h-5 text-green-400" />
                      <span className="font-bold text-white">Accuracy: {stats.avgConfidence}%</span>
                    </div>
                    <div className="flex items-center gap-2 bg-red-500/30 border border-red-400 rounded px-3 py-1">
                      <AlertTriangle className="w-4 h-4 text-red-400" />
                      <span className="font-bold text-white">High Risk: {stats.attack}</span>
                    </div>
                  </div>
                </>
              ) : (
                <div className="text-center py-12 text-gray-400">
                  <Brain className="w-16 h-16 mx-auto mb-4 opacity-30" />
                  <p className="text-sm">Awaiting analysis data</p>
                </div>
              )}
            </div>
          </div>
        </div>

        {/* Footer */}
        <div className="mt-6 flex items-center justify-between text-sm text-gray-400">
          <div className="flex items-center gap-4">
            <span>Use via API</span>
            <span className="text-gray-600">|</span>
            <span>Built with Gradio </span>
          </div>
          <button className="flex items-center gap-2 hover:text-blue-400 transition-colors">
            <Settings className="w-4 h-4" />
            Settings
          </button>
        </div>
      </div>
    </div>
  );
};

export default WirelessSecurityDashboard;
